package DSA.Bucket3._02_strings;

//https://www.geeksforgeeks.org/problems/encrypt-the-string-21117/1
public class _08_EncryptString {

	public static String reverseString(String s) {
		if (s.length() == 1)
			return s;

		return reverseString(s.substring(1)) + s.charAt(0);
	}

	public static String encrypt(String str) {

		int l = 0;
		String res = new String();

		for (int r = 1; r < str.length(); r++) {
			if (str.charAt(r) != str.charAt(l)) {
				int count = r - l;
				res += str.charAt(l) + reverseString(Integer.toHexString(count));
				l = r;
			}
		}

		res += str.charAt(l) + reverseString(Integer.toHexString(str.length() - l));// for last segment

		return reverseString(res);
	}

	public static void main(String[] args) {
		// String s = "aaaaaaaaaaa";
		// String s = "abc";
		String s = "";
		System.out.println(encrypt(s));
	}
}