package DSA._02_strings;

//https://www.geeksforgeeks.org/problems/encrypt-the-string-21117/1
public class _08_EncryptString {

	public static void main(String[] args) {

		// String s = "aaaaaaaaaaa";
		// String s = "abc";

		String s = "";
		int count = 0;
		int j = 0;
		String str = "";

		for (int i = 0; i < s.length(); i++) {

			if (s.charAt(i) == s.charAt(j))
				count++;

			else {
				str += strVal(s, j, count);
				j = i;
				count = 1;
			}
		}

		str += strVal(s, j, count);
		System.out.println(reverseString(str));

	}

	public static String strVal(String s, int j, int count) {
		return s.charAt(j) + reverseString(Integer.toHexString(count));
	}

	public static String reverseString(String s) {

		if (s.length() == 1)
			return s;

		return reverseString(s.substring(1)) + s.charAt(0);

	}
}